mapper.update:
update shopping_cart
set number = #{number},
    amount = #{amount}
where id = #{id}

mapper.delete:
delete
from shopping_cart


        and dish_id = #{dishId}


        and setmeal_id = #{setmealId}


        and dish_flavor = #{dishFlavor}

mapper.select:
select *
from shopping_cart


        and dish_id = #{dishId}


        and setmeal_id = #{setmealId}


        and dish_flavor = #{dishFlavor}
